components/hello.js (jsx)'use client'
export function Hello() {
  return <p>Hello!</p>
}
app/page.js (jsx)import dynamic from 'next/dynamic'
const ClientComponent = dynamic(() =>
  import('../components/hello').then((mod) => mod.Hello)
